package AtCoderBeginnerContest.no164.d;

import static org.junit.jupiter.api.Assertions.*;

import java.io.ByteArrayInputStream;
import java.io.ByteArrayOutputStream;
import java.io.PrintStream;
import org.junit.jupiter.api.Test;

class MainTest {

	@Test
	public void test1() throws Exception {
		String input = "1817181712114";
		String output = "3";
		assertIO(input, output);
	}

	@Test
	public void test2() throws Exception {
		String input = "14282668646";
		String output = "2";
		assertIO(input, output);
	}

	@Test
	public void test3() throws Exception {
		String input = "2119";
		String output = "0";
		assertIO(input, output);
	}

	@Test
	public void test100() throws Exception {
		String input = "1817181712114181718171211418171817121141817181712118171817121141817181712114181718171211418171817121";
		String output = "0";
		assertIO(input, output);
	}

	@Test
	public void test1000() throws Exception {
		String input = "";
		String output = "0";
		assertIO(input, output);
	}

	@Test
	public void test10000() throws Exception {
		String input = "";
		String output = "0";
		assertIO(input, output);
	}

	private void assertIO(String input, String output) throws Exception {
		ByteArrayInputStream in = new ByteArrayInputStream(input.getBytes());
		System.setIn(in);

		ByteArrayOutputStream out = new ByteArrayOutputStream();
		System.setOut(new PrintStream(out));

		Main.main(new String[0]);

		assertEquals(output + System.lineSeparator(), out.toString());
	}
}
